import React, { useState, useEffect } from 'react'

function DrinksHomePage() {
    const [searchItem, setSearchItem] = useState("");
    const [items, setItems] = useState([]);

    useEffect(() => {
        fetch(`https://www.thecocktaildb.com/api/json/v1/1/search.php?s=${searchItem}`)
            .then((response) => response.json())
            .then((data) => {
              console.log(data);
              setItems(data.drinks);
            })
    }, [])

    const searchBar = async(e) => {
      e.preventDefault();
      try {
        const url = `https://www.thecocktaildb.com/api/json/v1/1/search.php?s=${searchItem}`;
        const res = await fetch(url);
        const data = await res.json();
        console.log(data);
        setItems(data.drinks);
      }
      catch(e) {
        console.log(e);
      }
    }

    const changeHandle = (e) => {
      setSearchItem(e.target.value)
    }

    return (
      <>
        <form className="banner" onChange={searchBar} autoComplete="off">

          <input
            type="search"
            placeholder="Je cherche un cocktail"
            className='search'
            name="search"
            value={searchItem}
            onChange={changeHandle}
            >
            </input>
        </form>
        <div>
          {items || items > 0 ? (
          <div className='container'>
              <Drinks drinks={items} />
          </div>
          ) : (<NoDrink />)}
        </div>
      </>
    );
  }

export default DrinksHomePage
