import React from 'react'
import DrinksItems from './DrinksItems';

function DrinksDisplay({drinks}) {
    return(
        <div className='drink'>
            {drinks.map((val) =>
                <DrinksItems drinkItem={val}/> 
            )}
        </div>
    )
}

export default DrinksDisplay;
